    const searchBook=(evt)=>{
        if(evt.key==="Enter")
        {
            axios.get('https://www.googleapis.com/books/v1/volumes?q='+search+'&key=AIzaSyAnBI0GTa-enOpI8fTIGP6_xPap35FBooo'+'&maxResults=40')
            .then(res=>setData(res.data.items))
            .catch(err=>console.log(err))
        }
    }
    return(
        <>


            <div className="container">
                <div className="header">
                    <div class="logo">
                        <img src="../img/logofpt.png" alt=""/>
                    </div>
                    <div class="title">
                        <h1>THƯ VIỆN FPT POLYTECHNIC</h1>
                    </div>
                </div>
            </div>
        </>
    )
}
export default Main;
